    } else if (is.null(state$division)) {
      uni %>% filter(Region == state$region)
    } else {
      uni %>% filter(Region == state$region, Division == state$division)
    }
    
    # 2️⃣ Summarize by current level
    plot_summary <- if (is.null(state$region)) {
      plot_data %>%
