          JHS = sum(as.numeric(JHS.Enrolment), na.rm = TRUE),
          SHS = sum(as.numeric(SHS.Enrolment), na.rm = TRUE),
          .groups = "drop"
        )
    } else if (is.null(state$division)) {
      plot_data %>%
        group_by(group_col = Division) %>%
        summarise(
          ES = sum(as.numeric(ES.Enrolment), na.rm = TRUE),
          JHS = sum(as.numeric(JHS.Enrolment), na.rm = TRUE),
          SHS = sum(as.numeric(SHS.Enrolment), na.rm = TRUE),
          .groups = "drop"
        )
    } else {
