        position: -webkit-sticky !important; /* Safari */
        top: 0px !important;                 /* Stick to the very top */
        z-index: 4 !important;
        background-color: #ffffff !important; /* White background */
        box-shadow: 0 2px 6px rgba(0, 0, 0, 0.15) !important; /* Shadow */
