      --deped-light: #f4f6fa;
      --deped-white: #ffffff;
    }



    /* =====================================================
       SIDEBAR
       ===================================================== */
    .sidebar_erdb {
      width: 260px;
      background: var(--deped-blue);
      color: var(--deped-white);
      padding: 20px;
      border-radius: 0 12px 12px 0;
      box-shadow: 2px 0 8px rgba(0,0,0,0.15);
      position: sticky;
      top: 60px; /* Adjust top based on navbar height */
      height: calc(100vh - 60px); /* Adjust height based on navbar height */
      flex-shrink: 0;
      overflow-y: auto;
    }

    .sidebar_erdb h4 {
      color: var(--deped-gold);
      font-weight: 700;
      text-align: center;
      margin-bottom: 20px;
    }

    /* =====================================================
       SIDEBAR BUTTONS
       ===================================================== */
    .btn-card {
      background: rgba(255,255,255,0.1);
      border: 1px solid rgba(255,255,255,0.15);
      color: var(--deped-white);
      border-radius: 10px;
      text-align: left;
      padding: 12px 16px;
      display: flex;
      align-items: center;
      width: 100%;
      transition: all 0.2s ease-in-out;
    }

    .btn-card:hover,
    .btn-card:focus {
      background: var(--deped-gold) !important;
      color: var(--deped-blue) !important;
      transform: scale(1.02);
      box-shadow: 0 4px 10px rgba(0,0,0,0.2);
    }

    .btn-card h5 {
      display: inline-block;
      margin-left: 8px;
      font-size: 1.05rem;
      font-weight: 600;
    }



    /* =====================================================
       GLOBAL SCROLLBAR
       ===================================================== */
    ::-webkit-scrollbar {
      width: 10px;
    }

    ::-webkit-scrollbar-thumb {
      background:  #003366;
      border-radius: 10px;
    }


